mapper.select:
SELECT empid , empname , mgr , sal , deptid , hiredate FROM emp
AND empname = #{emp.empname}
ORDER BY empid

mapper.insert:

	SELECT NVL(MAX(empid), 0) + 1
	FROM emp

INSERT INTO emp
		(
			empid
			, empname
			, mgr
			, sal

			, deptid

			, hiredate
		)
	VALUES
		(

			#{empid}
			,#{empname}
			,#{mgr}
			,#{sal}

			,#{deptid}



					,#{hiredate}


					, sysdate


		)

mapper.update:
UPDATE emp

	sal = #{emp.sal}
	, mgr = #{emp.mgr}
	, deptid = #{emp.deptid}

	, empname = #{emp.empname}


WHERE empid = #{id}
